	private Object[][] getselect(List list) {
		Object[][] s = new Object[list.size()][6];
		for (int i = 0; i < list.size(); i++) {
			stuinfo stuinfo = (stuinfo) list.get(i);
			s[i][0] = stuinfo.getSno();
			s[i][1] = stuinfo.getCno();
			s[i][2] = stuinfo.getName();
			s[i][3] = stuinfo.getShuxing();
			s[i][4] = stuinfo.getCcredit();
			s[i][5] = stuinfo.getGrade();
		}
		return s;

	}

